import React, { useRef } from "react";
// import './NewProduct.css';

const Homescreen = (props) => {
	const nameInputRef = useRef();
	const descInpurRef = useRef();
	const priceInputRef = useRef();
	const cateInputRef = useRef();
	const itemsInputRef = useRef();
	const imageInputRef = useRef();

	const submitHandler = (e) => {
		e.preventDefault();

		const enteredName = nameInputRef.current.value;
		const enteredDesc = descInpurRef.current.value;
		const enteredPrice = priceInputRef.current.value;
		const enteredCategory = cateInputRef.current.value;
		const enteredTItems = itemsInputRef.current.value;
		const enteredTImageurl = imageInputRef.current.value;

		const productData = {
			name: enteredName,
			description: enteredDesc,
			price: enteredPrice,
			category: enteredCategory,
			countInStock: enteredTItems,
			imageUrl: enteredTImageurl,
		};
		console.log(productData);
		console.log(props);
		props.onAddProduct(productData);
	};

	function showImage(src, target) {
		var fr = new FileReader();

		fr.onload = function () {
			target.src = fr.result;
		};
		fr.readAsDataURL(src.files[0]);
	}
	function putImage() {
		var src = document.getElementById("select_image");
		var target = document.getElementById("target");
		showImage(src, target);
	}
	return (
		<div className="card">
			<form className="form" onSubmit={submitHandler}>
				<div className="control">
					<label htmlFor="name">Product Name: </label>
					<input type="text" required className="name" ref={nameInputRef} />
				</div>
				<div className="control">
					<label htmlFor="description">Product Description: </label>
					<textarea
						className="description"
						required
						cols="30"
						rows="3"
						ref={descInpurRef}
					/>
				</div>
				<div className="control">
					<label htmlFor="price">Product Price: </label>
					<input type="number" required className="price" ref={priceInputRef} />
				</div>
				<div className="control">
					<label htmlFor="category">Product Category: </label>
					<input type="text" required className="category" ref={cateInputRef} />
				</div>
				<div className="control">
					<label htmlFor="items">Number of Items: </label>
					<input
						type="number"
						required
						className="CountInStock"
						ref={itemsInputRef}
					/>
				</div>
				<img id="target" />
				<input type="file" id="select_image" name="image" onChange={putImage} />
				{/* <div className="control">
					<label htmlFor="image">Product Image Url: </label>
					<input type="url" required className="imageUrl" ref={imageInputRef} />
				</div> */}
				<div className="actions">
					<button>Add Product</button>
				</div>
			</form>
		</div>
	);
};

export default Homescreen;
